// import logo from './logo.svg';
// import './App.css';

import { useEffect, useState } from "react";
import Todo from "./Todo";
import Todoform from "./Todoform";


function App() {
  const [mytodos, setMyTodos] = useState([]);
  //view all todos by fetching from the database
  //set todos to a state variable

useEffect(() => {
  fetch("http://localhost:3000/todos")
    .then(res => res.json())
    .then(data => setMyTodos((mytodos) => data))
  }, [])

  const todoList = mytodos.map((elem,index)=> {
    return (<Todo 
      key={index}
      tasks={elem.tasks}
      duedate={elem.duedate}
      category={elem.category}
    />)
  })

  function handleTask (data) {
    let addeTodo = [...mytodos, data]
    setMyTodos((mytodos) => addeTodo)

  }

  return (
    <>
      <h1>This another form</h1>
      <Todoform />
      <Todoform getTask={handleTask}/>
      <h1>Hello</h1>
      <>{todoList}</>

    </>
  );
}

export default App;
